def format(string_list):
	i = 0
	formatted_string_list = []
	
	for element in string_list:
		string = ''
		split_sentence = element.split()
		for word in split_sentence:
			if word in ['verb', 'adj.', 'noun', 'pron.', 'interj.', 'adv.']:
				word += ':'
			if word[-1] == ';':
				word += '\\n'
			if word[-2:] == '\\n':
				string += word + ''
			else:
				string += word + ' '

		formatted_string_list.append(string)
	
	with open("definitions_arrayFinal.txt", "w") as writefile:
		writefile.write("{")
		for element in formatted_string_list:
			writefile.write(f"\"{element}\",")
		writefile.write("}")

format(string_list)


